import { createReducer, on } from '@ngrx/store';
import { setRecipesAction } from '../actions/recipes.actions';

let initialState = { recipes: [] };

export const recipeReducer = createReducer(
  initialState,
  on(setRecipesAction, (state, action) => {
    let newState = { ...state };
    return newState;
  })
);
