import { createAction, props } from '@ngrx/store';
import { Recipe } from 'src/app/recipes/recipe.model';

export const SET_RECIPES = '[Recipes] Set Recipes';

export const setRecipesAction = createAction(
  SET_RECIPES,
  props<{ recipes: Recipe[] }>()
);
